mapper.insert:
INSERT
  INTO TBL_MEMBER A
(
  A.MEMBER_NO
, A.MEMBER_ID
, A.MEMBER_PWD
, A.MEMBER_NAME
, A.GENDER
, A.EMAIL
, A.PHONE
, A.ADDRESS
, A.AGE
, A.ENROLL_DATE
)
VALUES
(
  SEQ_MEMBER_NO.NEXTVAL
, #{ memberId }
, #{ memberPwd }
, #{ memberName }
, #{ gender}
, #{ email }
, #{ phone }
, #{ address }
, #{ age }
, SYSDATE
)

mapper.update:
		UPDATE
               TBL_MEMBER A
           SET A.MEMBER_PWD = #{ memberPwd }
         WHERE A.MEMBER_ID = #{ memberId }

		UPDATE
               TBL_MEMBER A
           SET A.EMAIL = #{ email }
         WHERE A.MEMBER_ID = #{ memberId }

		UPDATE
               TBL_MEMBER A
           SET A.PHONE = #{ phone }
         WHERE A.MEMBER_ID = #{ memberId }

		UPDATE
               TBL_MEMBER A
           SET A.ADDRESS = #{ address }
         WHERE A.MEMBER_ID = #{ memberId }

mapper.delete:
		DELETE
          FROM TBL_MEMBER A
         WHERE A.MEMBER_ID = #{ memberId }
